<html><h3>Online Nodepad</h3><form method='POST'>Filename  :<input type="text" name="fname" style="width:400"><br><?php $fname=$_POST['fname'];if(isset($_POST['save')){	$content=$_POST['content'];	$content=stripslashes($content);	if(strlen($fname)!=0)	{		save($fname,$content);	}}else if(isset($_POST['open'])){	$content=file_get_contents($fname);	echo('<textarea name="content" rows="20" cols="100">'.$content.'</textarea>');}else{	echo('<textarea name="content" rows="20" cols="100"></textarea>');}
function save($fn,$cont){	$file=fopen($fn,'w+b');	fwrite($file,$cont);}?><input type="submit" name="save" value="Save"><input type="submit" name="open" value="Open"></form><br><a href="/">Go back</a></html>