<?php $ch = curl_init();curl_setopt($ch, CURLOPT_HEADER, 0);curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);$file=fopen('ftpservers.txt','w');$str='';for($i=0;$i<=20;$i+=10){	curl_setopt($ch,CURLOPT_URL,"http://www.google.com/search?q=inurl:ftp+inurl:aspx+inurl:admin&noj=1&start=".$i);		$page=curl_exec($ch);		$str.=$page;}			$str=str_replace('</b>','',$str);	$addr='';		$startpos=1;	$endpos=0;	$res='';	while($startpos)	{		$startpos=strpos($str,'ftp://',$endpos+1);			$endpos=strpos($str,'/',$startpos+8);		$addr=trim(substr($str,$startpos,$endpos-$startpos));		if(substr_count($res,$addr)==0)		{			$res.= $addr."\n";		}}fwrite($file,$res);fclose($file);echo $res;curl_close($ch);?>